import React, { Component } from "react";
import contacts from "./contacts.json";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import "./Contacts.css";

class Contacts extends Component {
  state = {
    contacts: contacts.slice(0, 5),
    random: contacts[Math.ceil(Math.random() * contacts.length)]
  };

    randomizeCelebrity = () => {

        let newContactsArray = this.state.contacts.filter(contact => contact.id !== this.state.random.id)
        this.setState({
            contacts: newContactsArray,
            random: contacts[Math.ceil(Math.random() * contacts.length)]
        });
    };

    render() {
        console.log("Contacts -> randomizeCelebrity -> contacts", this.state.contacts)

    return (
      <div>
        <button onClick={this.randomizeCelebrity}>Add Random Celebrity</button>
        {/* <button onClick={console.log(this.newContactsArray)}>Add Random Celebrity</button> */}



        <TableContainer component={Paper}>
          <Table aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="center">Picture</TableCell>
                <TableCell align="center">Name</TableCell>
                <TableCell align="center">Popularity</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {this.state.contacts.map(contact => (
                <TableRow key={contact.id}>
                  <TableCell align="center">
                    <img className="image" src={contact.pictureUrl} alt="" />
                  </TableCell>
                  <TableCell align="center">{contact.name}</TableCell>
                  <TableCell align="center">{contact.popularity}</TableCell>
                </TableRow>
              ))}
            </TableBody>
            <TableBody>
                <TableRow key={this.state.random.id}>
                  <TableCell align="center">
                    <img className="image" src={this.state.random.pictureUrl} alt="" />
                  </TableCell>
                  <TableCell align="center">{this.state.random.name}</TableCell>
                  <TableCell align="center">{this.state.random.popularity}</TableCell>
                </TableRow>
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    );
  }
}

export default Contacts;
